void mainImage(out vec4 f,vec2 w)
{
    vec4 p = (vec4(w/iResolution.xy,.61+sin(iTime*.3)*.1,1)-.5)*.6, d = p, a = p - p, v;
    p.z += iTime*(sin(iTime*.1)+3.);
    for(int i = 0; i++ < 330;)
        v = texture(iChannel0, (p.xy + vec2(37, 17) * floor(p.z)) / 256.),
		v.w =  mix(v.y, v.x, fract(p.z)),
        a += v*v.w*.01,
        p += d;

    f = pow(a, vec4(12));
}
