//By Nestor Vina
#define j  + texture(iChannel0,(c.xz+iTime/m)/2e2*++m).r*.4/m
void mainImage( out vec4 c, vec2 f ){
	c -= c; 

    for(float i = 1.,m; i++ < 1e2; m =.0)
        m = j j j j j j,
        c.xyz += vec3( f / iResolution.y - .5, .5 ) * ( -c.y + 1. + m * m ) * .2,
        c.a += m / 2e2;

    c = texture( iChannel1, c.xz * .4 ) * .1 + vec4( 0, .7, .8, 1 )*( pow( c.a, 8.) * 4e3 + .7 );
}
