vec3 sphereToCart(vec3 sphere) {
    float cp = cos(sphere.x);
    float sp = sin(sphere.x);
    float ct = cos(sphere.y);
    float st = sin(sphere.y);
    float r = sphere.z;
    return vec3(r * cp * ct, r * st, r * sp * ct);
}

vec3 cartToSphere(vec3 cart) {
    float r = length(cart);
    vec3 n = cart / r;
    return vec3(atan(n.x, n.z), asin(n.y), r);
}


const float SQRT_2 = 1.414213562373095;
const float ISQRT_2 = 0.70710678118655;
const float PI = 3.141592653589793;

const vec3 coords[8] = vec3[8](
    vec3(1, 0, ISQRT_2),
    vec3(-1, 0, ISQRT_2),
    vec3(0, 1, -ISQRT_2),
    vec3(0, -1, -ISQRT_2),
    vec3(1, 0, -ISQRT_2),
    vec3(-1, 0, -ISQRT_2),
    vec3(0, 1, ISQRT_2),
    vec3(0, -1, ISQRT_2)
);

const int faces[12] = int[12](
    0, 1, 2,
    0, 2, 3,
    0, 3, 1,
    1, 2, 3
);

const int dual_faces[12] = int[12](
    4, 5, 6,
    4, 6, 7,
    4, 7, 5,
    5, 6, 7
);

bool intersects(
    in vec3 orig, in vec3 dir, in vec3 vertices[3],
    out vec3 I, out vec2 st)
{
    vec3 u, v, n;
    vec3 w0, w;
    float r, a, b;

    u = vertices[1] - vertices[0];
    v = vertices[2] - vertices[0];
    n = cross(u, v);

    w0 = orig - vertices[0];
    a = -dot(n, w0);
    b = dot(n, dir);
    if (abs(b) < 1e-5) return false;

    r = a / b;
    if (r < 1e-5) return false;

    I = orig + r * dir;
    float uu, uv, vv, wu, wv, D;
    uu = dot(u, u);
    uv = dot(u, v);
    vv = dot(v, v);
    w = I - vertices[0];
    wu = dot(w, u);
    wv = dot(w, v);
    D = uv * uv - uu * vv;

    float s, t;
    s = (uv * wv - vv * wu) / D;
    if (s < 0. || s > 1.) return false;
    t = (uv * wu - uu * wv) / D;
    if (t < 0. || t > 1.) return false;

    st = vec2(s, t);
    if (r > 1e-5) return true;

    return false;
}


bool intersectsShape(
    in vec3 orig, in vec3 dir, in mat3 rot, 
    out int face, out vec3 I, out vec2 st) 
{
    bool found = false;
    vec3 found_I;
    vec2 found_st;
    for(int i = 0; i < 4; i++) {
        if (intersects(orig, dir, vec3[3](
            	rot * coords[faces[3 * i]],
            	rot * coords[faces[3 * i + 1]],
            	rot * coords[faces[3 * i + 2]]
        	), found_I, found_st)) {

            if (!found || length(st) > length(found_st)) {
                found = true;
                st = found_st;
                I = found_I;
                face = i;
            }
        }
    }

    return found;
}



bool intersectsDual(
    in vec3 orig, in vec3 dir, in mat3 rot, 
    out int face, out vec3 I, out vec2 st) 
{
    bool found = false;
    vec3 found_I;
    vec2 found_st;
    for(int i = 0; i < 4; i++) {
        if (intersects(orig, dir, vec3[3](
            	rot * coords[dual_faces[3 * i]],
            	rot * coords[dual_faces[3 * i + 1]],
            	rot * coords[dual_faces[3 * i + 2]]
        	), found_I, found_st)) {

            if (!found || length(st) > length(found_st)) {
                found = true;
                st = found_st;
                I = found_I;
                face = i;
            }
        }
    }

    return found;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mat3 rot = mat3(
        sin(iTime), cos(iTime), 0.,
       	cos(iTime), -sin(iTime), 0.,
        0., 0., 1.
    );
    //mat3 rot = mat3(1., 0., 0., 0., 1., 0., 0., 0., 1.);

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 cart = sphereToCart(vec3(2. * PI * uv.s, PI * (uv.t - 0.5), 1.));
    //vec3 cart = vec3(uv, 0.5);

    int face = 0;
    vec3 p = vec3(0.);
    vec2 st = vec2(0.);

    if (intersectsShape(vec3(0., 0., 0.), cart, rot, face, p, st)) {
        float x = 1. - min(st.s + st.t, min(1. - st.s, 1. - st.t));

        vec3 v = vec3(st.s+st.t, 1. - st.s, 1. - st.t);
        x = 1. - x * x * x * x * x * x;

        fragColor = vec4(x * (0.5 * normalize(coords[face]) + 0.5), 1.);
        //fragColor = vec4(x, x, x, 1.);
    } else {
        fragColor = vec4(0., 0., 0., 1.);
    }

     if (intersectsDual(vec3(0., 0., 0.), cart, rot, face, p, st)) {
        float x = 1. - min(st.s + st.t, min(1. - st.s, 1. - st.t));

        vec3 v = vec3(st.s+st.t, 1. - st.s, 1. - st.t);
        x = 1. - x * x * x * x;

        fragColor = x * fragColor;
        //fragColor = vec4(x, x, x, 1.);
    }
}
