float gr(vec2 uv){
    float a=sin(uv.x*4.+iTime*0.4)*0.3;
    return smoothstep(a-.2,a+.2,uv.y);
}

const float is3=1./sqrt(3.);
mat2 sk=mat2(2.*is3,is3,0.,1.0)*10.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-.5;
    uv.x *= iResolution.x/iResolution.y;
    float gunsk=gr(uv);
    vec2 suv=sk*uv;
    vec2 fl=inverse(sk)*floor(suv);
    vec2 fr=fract(suv);
    fl+=(vec2(.75,.25)+step(fr.x,fr.y)*vec2(-.5,.5))/10.;
    float gsk=gr(fl);
    float w=fract(uv.x+iTime);
    w=smoothstep(.8,.85,w)-smoothstep(.95,1.,w);
    vec3 c=vec3(mix(gunsk,gsk,w));
	fragColor = vec4(c,1.0);
}
