//addapted from this opengl method:
//http://stackoverflow.com/questions/16989181/whats-the-correct-way-to-draw-a-distorted-plane-in-opengl

vec2 _Values[4];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    //populate corners destiny positions
    _Values[0].x = 0.0;
    _Values[0].y = 0.0;
    _Values[1].x = 1.0;
    _Values[1].y = 0.0;
    _Values[2].x = 1.0;
    _Values[2].y = 1.0;
    _Values[3].x = 0.0;
    _Values[3].y = 1.0;

    //use keys 0, 1, 2, 3 to determine what corner you want to drag with mouse
    float n = 0.;
    bool isKeyPressed = false;
    for(int i=0; i<4; i++){
        if (texture(iChannel1,vec2(49.5+float(i),0.5)/256.).x > 0.0)
        {
            _Values[i].x = iMouse.x / iResolution.x;
            _Values[i].y = iMouse.y / iResolution.y;
            isKeyPressed = true;
        }
    }
    if(!isKeyPressed) {
        _Values[0].x = iMouse.x / iResolution.x;
        _Values[0].y = iMouse.y / iResolution.y;
    }

    //distort/crop
    float linePt0x = (1.0 - uv.y) * _Values[0].x + uv.y * _Values[3].x;
    float linePt0y = (1.0 - uv.y) * _Values[0].y + uv.y * _Values[3].y;
    float linePt1x = (1.0 - uv.y) * _Values[1].x + uv.y * _Values[2].x;
    float linePt1y = (1.0 - uv.y) * _Values[1].y + uv.y * _Values[2].y;
    float ptx = (1.0 - uv.x) * linePt0x + uv.x * linePt1x;
    float pty = (1.0 - uv.x) * linePt0y + uv.x * linePt1y;

    uv.x = ptx;
    uv.y = pty;


    fragColor = texture(iChannel0, uv);
}
