// Blackbird Carbon Logo
// Copyright (c) 2016 Blackbird Interactive Inc.

bool inHexagon(in vec2 p, in vec2 p0, in float r)
{
    vec2 delta = abs(p - p0);
    return (delta.y <= r * sqrt(3.0) / 2.0) && (3.0 * delta.x + sqrt(3.0) * delta.y <= 3.0 * r);
}

bool inLogo(in vec2 p, in vec2 p0, in float r, in float slit)
{
    bool in1 = inHexagon(p, p0, r);
    bool in2 = inHexagon(p, vec2(p0.x + r, p0.y), r);
    bool in3 = inHexagon(p, vec2(p0.x + r + slit, p0.y), r);

    return in1 && (!in2 || in3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoomStartTime = 1.0;
    float zoomEndTime = zoomStartTime + 1.5;
    float slitStartTime = zoomEndTime + 0.25;
    float slitEndTime = slitStartTime + 0.75;

	vec2 center = iResolution.xy * vec2(0.5, 0.5);
    float maxsize = iResolution.y * 0.4;
    float maxslit = maxsize / 6.0;
    float size = maxsize * smoothstep(zoomStartTime, zoomEndTime, iTime);
    float slit = maxslit * smoothstep(slitStartTime, slitEndTime, iTime);

    bool isLogo = inLogo(fragCoord, center, size, slit);

    vec4 foreground = vec4(113.0/255.0, 112.0/255.0, 117.0/255.0, 1.0);	// BBI grey
    vec4 background = vec4(0.0, 0.0, 0.0, 1.0);

    fragColor = mix(background, foreground, float(isLogo));

}
