mat2 rotate(float r){
   return  mat2( cos(r), -sin(r),
                sin(r),  cos(r) ); 
}
void mainImage( out vec4 o, in vec2 p )
{
    vec2 r = iResolution.xy;
    vec2 vm = (p-r*.5)/min(r.x,r.y)*10.;
    vec2 m = iMouse.xy;

    vm = vec2(vm.x/sin(3.14*0.5),vm.y/sin(3.14*0.5*(m.y/r.y+0.05)));
    vm = rotate(3.14*(m.x/r.x*4.+0.25))*vm;
    o = vec4(1.);


    if(abs(vm.x)<4. && abs(vm.y)<4.)
        o = vec4(floor(fract(vm.x)+0.5),floor(fract(vm.y)+0.5),floor(fract(vm.x)+fract(vm.y)),1.);
    else if(abs(vm.x)<4.1 && abs(vm.y)<4.1)
            o = vec4(vec3(.8),1.);

}
