float mod289(float x)
{
    return x - floor(x * (1.0 / 289.0)) * 289.0;
}


float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(float p){
    float fl = floor(p);
  	float fc = fract(p);
    fc = fc * fc * (3.0 - 2.0 * fc);
    return mix(rand(fl), rand(fl + 1.0), fc);
}

float noise(vec2 n) {
    const vec2 d = vec2(0.0, 1.0);
  	vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
    return mix( mix(rand(b), 		rand(b + d.yx), f.x), 
                mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}


float line(vec2 p, vec2 a, vec2 b)
{
     vec2 a2b = b - a;
     vec2 a2p = p - a;     

     // å› ä¸ºæŠ•å½± proj(a2p,a2b) ç­‰äºŽ dot(a2p,a2b) / length(a2b)
     // åœ¨æŠŠæŠ•å½±çš„å€¼å’Œa2bå‘é‡åšæ¯”ä¾‹å…³ç³»ã€‚
     // æ‰€ä»¥proj(a2p,a2b)/length(a2b) ç­‰äºŽ dot(a2p,a2b)/dot(a2b,a2b)

     float h = clamp( dot(a2p,a2b)/dot(a2b,a2b), 0.0, 1.0 );
     vec2 p1 = mix( a, b, h );
     return length( p1 - p );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;


    //uv.y += noise(uv.x*40.0+iTime*5.) * 0.02;
    uv += noise(uv*40.0+iTime*5.) * 0.02;

    float t = line(uv, vec2(0.02,0.5),vec2(1.75,0.5));
    //float t = line(uv, vec2(0.5,0.02),vec2(0.5,1.0));
	fragColor = vec4(0.05, 0.15, 1.0, 1.0)*.015/t;

    //fragColor = vec4(vec3(noise(uv*50.)), 1.0);

}
