void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 t = texture(iChannel0,uv);
	//t = pow(t,vec4(.7));
    if (fragCoord.x<iMouse.x) {
        fragColor = t*.5;
    } else {
    	int xm = int(mod(fragCoord.x+fragCoord.y*2.,4.0));
	    fragColor = vec4(0.);
		if (xm==0) fragColor.r =  t.r;
		if (xm==1) fragColor.g =  t.g;
		if (xm==2) fragColor.b =  t.b;
    }
}
