float Band(float t, float start, float end, float blur) 
{
    float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep(end + blur,   end - blur,   t);

    return step1*step2;
}

float Rect(vec2 uv, float l, float r, float bottom, float top, float blur)
{
    float band1 = Band(uv.x, l,        r, blur);
    float band2 = Band(uv.y, bottom, top, blur);

    return band1 * band2;
}

float r(vec2 uv, float xBegin, float yBegin, float h, float w) 
{
    float xEnd = xBegin + w;
    float yEnd = yBegin + h;

    return Rect(uv, xBegin, xEnd, yBegin, yEnd, 0.000001);
}

float draw(vec2 uv, float x, float y, float w, float h, int digit)
{
    float f = 0.0;

	if(digit == 0)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w); f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 0., y + h * 1., h, w); f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 1)
	{
		f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 2)
	{
	    f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 0., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 3)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
    	f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 4)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w); f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 5)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
        f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 6)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
        f += r(uv, x + w * 0., y + h * 1., h, w); f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 7)
	{
		f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
        f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 8)
	{
	    f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w); f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 0., y + h * 1., h, w); f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	else if(digit == 9)
	{
	    f += r(uv, x + w * 0., y + h * 4., h, w); f += r(uv, x + w * 1., y + h * 4., h, w); f += r(uv, x + w * 2., y + h * 4., h, w);
	    f += r(uv, x + w * 0., y + h * 3., h, w); f += r(uv, x + w * 2., y + h * 3., h, w);
	    f += r(uv, x + w * 0., y + h * 2., h, w); f += r(uv, x + w * 1., y + h * 2., h, w); f += r(uv, x + w * 2., y + h * 2., h, w);
	    f += r(uv, x + w * 2., y + h * 1., h, w);
	    f += r(uv, x + w * 0., y + h * 0., h, w); f += r(uv, x + w * 1., y + h * 0., h, w); f += r(uv, x + w * 2., y + h * 0., h, w);
	}

	return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = gl_FragCoord.xy / iResolution.xy;

	uv *= 3.;
	vec2 idx = floor( uv );

	uv = fract( uv );

    uv.x *= iResolution.x/iResolution.y;

	float h  = 0.025;
	float w = h;

    vec3 col = vec3(0.0);

    float f = 0.0;

    float xx = w;
	float yy = h * 18.;

	float xxx = xx;
	float yyy = yy;

	float a;

	vec3 test = vec3(iTime, 1.0 / 3.0, -1389.4235);
	vec3 v = test;

	if(idx.x == 1. && idx.y == 2.)
	{
	    a = v.x;
		yyy -= h * 18.0;
	}

	else if(idx.x == 1. && idx.y == 1.)
	{
	    a = v.y;
	}

	else if(idx.x == 1. && idx.y == 0.)
	{
	    a = v.z;
		yyy += h * 17.0;
	}

	else
	{
	    fragColor = vec4(0.0);
	    return;
	}

	f = 0.0;

	// Minus 
	if(a < 0.0)
	{
	    f += r(uv, xxx + w * 0., yyy + h * 2., h, w);
    	f += r(uv, xxx + w * 1., yyy + h * 2., h, w);
	    f += r(uv, xxx + w * 2., yyy + h * 2., h, w);
		a = abs(a); 
    }

	xxx += w * 4.0;

	int e  = int(mod(a,         10.));
	int z  = int(mod(a /   10., 10.));
	int hu = int(mod(a /  100., 10.));
	int t  = int(mod(a / 1000., 10.));

	int n1 = int(mod(a *    10., 10.));
	int n2 = int(mod(a *   100., 10.));
	int n3 = int(mod(a *  1000., 10.));
	int n4 = int(mod(a * 10000., 10.));

    f += draw(uv, xxx, yyy, w, h, t);
	xxx += w * 4.0;
    f += draw(uv, xxx, yyy, w, h, hu);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, z);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, e);
	xxx += w * 4.0;

	f += r(uv, xxx + w * 0.0, yyy + h * 0.0, h, w);

	xxx += w * 2.0;
	f += draw(uv, xxx, yyy, w, h, n1);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, n2);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, n3);
	xxx += w * 4.0;
	f += draw(uv, xxx, yyy, w, h, n4);

    col = vec3(0.0, 1.0, 0.0) * f;

    fragColor = vec4(col, 1.0);
}
