#define M_PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ms = 2.0 + floor(iMouse.xy / iResolution.xy * vec2(7.0, 5.0));

    vec2 norm = fragCoord / iResolution.x;
    vec2 cent = norm - vec2(0.5, iResolution.y / iResolution.x / 2.0);


    float rad_x = atan(cent.y, cent.x) * ms.x / M_PI / 2.0;


    float inte;
    float frac = modf(length(cent) * 2.0, inte);

    float rad_y = pow(length(cent) * 2.0, 0.2) * 6.0 + rad_x / ms.x * ms.y;

    // float rad_y = pow(length(cent) * 2.0, 0.2) * 6.0 + rad_x / ms.x * ms.y;

    vec2 uv = vec2(rad_x - iTime*ms.x/16.0, rad_y);

    vec3 col = 0.5 + 0.5*cos(uv.xyx * M_PI * 2.0 + vec3(0,2,4));

    vec4 color = texture(iChannel0, uv);
    color = mix(texture(iChannel1, uv), color, color.a);

    fragColor = vec4(col, 1.0); // try this!
    fragColor = vec4(mod(uv, vec2(1.0)), 0, 1);
    fragColor = vec4(pow(length(cent), 0.4) * color.rgb, 1.0);
}
