float rand(float a, float b)
{
    float truc =sin(sin(0.865984*a)+0.1984984*b)*6984.65164;
    return truc-floor(truc);
}

float interp(float a, float b, float p)
{
    return mix(a, b, p*p*(3.-2.*p));
}

float noise(in vec2 coord, in float res, in float seed)
{

    vec2 uv = (coord/max(iResolution.x,iResolution.y))*res;
    uv = uv + vec2(50.*rand(seed, 5.*seed), 50.*rand(3.*seed, seed));
    float x = floor(uv.x);
    float y = floor(uv.y);
    float dl = rand(x,y);
    float dr = rand(x+1.0,y);
    float ul = rand(x,y+1.0);
    float ur = rand(x+1.0,y+1.0);
    float d = interp(dl, dr, uv.x-x);
    float u = interp(ul, ur, uv.x-x);
    float g = interp(d, u, uv.y-y);
    return  g;
}

vec4 grey(in vec2 v)
{
    const int size = 4;
    //intensit√© resolution seed
    vec3[size] param = vec3[size] (vec3(15.,10.,354.198),
                                   vec3(12.,20.,687.156),
                                   vec3(9.,40.,549.1258),
                                   vec3(3.,70.,1987.187));
    float color = 0.;
    float inten = 0.;
    for(int i = 0; i<size ; i++)
    {
        inten += param[i].x;
    	color += param[i].x*noise(v,param[i].y,param[i].z);
    }
    color = color/inten;
    return color*vec4(1.,1.,1.,0.);
}

vec4 truc(in vec2 v)
{
    return vec4(noise(v,52.,21.56887)/2.+noise(v,115.,175.6524)/3.+noise(v,231.,1.)/67.65147,
                noise(v,48.,121.4344)/2.+noise(v,106.,100.3247)/3.+noise(v,221.,1.)/204.6488,
                noise(v,61.,65.32168)/2.+noise(v,120.,16.65778)/3.+noise(v,214.,1.)/636.5499,0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = grey(fragCoord);
}
