//---------------------------------------------------------

#define FONT_SIZE1 1.0

float char(vec2 tpos, int ch)           // draw character
{
  vec4 f = any(lessThan(vec4(tpos,1,1), vec4(0,0,tpos))) ? vec4(0) 
    : texture(iChannel3,0.0625*(tpos + vec2(ch - ch/16*16,15 - ch/16)));  
  return f.x * (f.y+0.5) * (f.z+1.0);   // 3d
}
//---------------------------------------------------------
float QuestionMark (in vec2 fragCoord)  // return char intensity
{
  vec2 tp = 0.5 + (fragCoord.xy - 0.5*iResolution.xy) / iResolution.y; 
  return char(tp/FONT_SIZE1, 63);
}
//---------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord / iResolution.xy;
  vec4 col = 0.5 + 0.5*cos(iTime +uv.xyxy +vec4(0,2,4,1));
  fragColor = col * QuestionMark(fragCoord);
}
