float tact = 5.811;

float map(vec3 p){
    float pz = p.z;
    p.x = mod(p.x, 3.0)-1.5;
    p.y = mod(p.y, 3.0)-1.5;
    p.z = mod(p.z, 4.0)-2.0;

    float c=sin(iTime*0.3),s=cos(iTime*0.3);
    p.xy=mat2(c,s,-s,c)*p.xy;

    if(p.x<0.0){
        p.x = -p.x;
    }
    float strength = 0.06;
    return (length(p+vec3(-0.7,-0.5,0.0)) - 1.0+strength*sin(iTime*tact*2.0))+(length(p+vec3(0.7,0.5,0.0)) - 1.0+strength*sin(iTime*tact));
}
//
float trace(vec3 o, vec3 r){
    float t = 0.0;
    const int maxSteps = 32;
    for (int i = 0; i < maxSteps; i++){ 
        vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv*2.0 -1.0;
    uv.x *= iResolution.x / iResolution.y;
    float c=cos(iTime*0.3-1.621),s=sin(iTime*0.3-1.621);
	uv=mat2(c,s,-s,c)*uv;

    vec3 r = normalize(vec3(uv, 1.0));
    vec3 o = vec3(0.0,0.0,-3.0+iTime+0.3*sin(iTime*tact*1.0));


    float t = trace(o,r);
    float fog = 1.0 / (1.0 + t * t * 0.1);
    vec3 fc = vec3(fog);

	fragColor = (vec4(fc,1.0)*vec4(4.0,1.2,2.5,1.0)+vec4(0.5,0.1,0.0,1.0))*(clamp(iTime*0.3,0.0,1.0)+0.6*(sin(iTime)));
}
