void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = iResolution.xy;
    vec2 uv = (fragCoord -.5*R)/R.y;
    float ko = fract(ceil(iTime+0.28)/60.)*6.283185306;

    vec2 newuv = vec2(0.0,0.0);

    float c = cos(ko);
    float s = sin(ko);

    uv = mat2(c,s,-s,c)*uv;

	float rad = 0.0;

    rad = uv.y>0.0?smoothstep(0.02,0.005,abs(uv.x))*smoothstep(0.0,0.02,abs(uv.y)):0.0;
    rad *= smoothstep(0.15,0.156,clamp(0.1/length(uv)-0.08,0.0,1.0));
    rad += 0.2*smoothstep(0.14,0.15,clamp(0.1/length(uv)-0.08,0.0,1.0));


    //rad = ceil(rad*60.0)/19.0;






    fragColor = vec4(rad);
}
