void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord / iResolution.xy);
    fragColor = texture(iChannel0, uv);
}

#define ITERS 6000.0
#define INFINITY 2500000000.0

#define R(a) mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1 for y)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 c = uv;
    // uv2 = -= vec2(0.5, 0.0);
    c *= 1.0 - 0.99 * cos(iTime * 0.661);
    c += 0.7 * vec2( cos(iTime * 0.8),  sin(iTime * 0.8));
    c *= R(iTime * 0.3);

    float i;
    vec2 z = c;
    for (i = 0.0; i < ITERS; ++i) {
        if (dot(z,z) >= INFINITY)
            break;
        z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + c; 
    }

    // Output to screen
    if (i >= ITERS)
    	fragColor = texture(iChannel0,
               (fragCoord / iResolution.xy - 0.5) * R(0.01) * 1.05 + 0.5) * 0.9;
    else {
        float ii = (i / ITERS);
    	fragColor = vec4(
            pow(ii, .3), 
            pow(ii, .25),
            pow(ii, .1),
            1.) * 2.;
    }
}
