void mainImage( out vec4 O, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy,
    f = (uv - 0.5) * 1.5;
    f.x *= iResolution.x/iResolution.y;
    vec2 center = iMouse.y>0.0 ? iMouse.xy/iResolution.xy * 4.0 -2. : vec2(0.5) * 4.0 -2.;   
    float n = atan(f.x,f.y);
    float angle = atan(f.x,f.y),
     t = iTime * .3;
    float  d = distance(center,f);
    float j = d*3.0;
    vec2 offset = vec2(sin(j), cos(j) )* 0.5;
    center*=offset;
    d = distance(center,f);
    float e = sin(t*3.0);
    d += d < e ? -t : t;
    angle+=e;
    float big = sin( d*2.5 ),
     medium = sin(d*7.3)+.7,
     small = sin(d*123.)+1.1,
     r = medium - big * small,
     c = smoothstep(r-.1, r+.91,  sin(t*.37));
    c = iMouse.z > 0. ? c : 1.-c;
    O = vec4(c);
}
