void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float wind = sin(0.76 * iTime);
    vec2 fromCenter = uv - vec2(0.47 + 0.2 * wind, 0.7);

    vec3 col = mix(vec3(1.0, 0.4, 0.1), vec3(1.0, 0.8, 0.3), sqrt(uv.y));

    float r = 0.2 + 0.14 * cos( atan(fromCenter.y, fromCenter.x) * 10.0 + (20.0 * fromCenter.x + 10.0 * wind) + 1.0);
    col *= smoothstep(r, r+0.01, length(fromCenter));

    r = 0.015;
    r += 0.002 * cos(120.0 * fromCenter.y);
    r += exp(-50.0 * (uv.y-0.015));

    col *= 1.0 - (1.0 - smoothstep(r, r+0.002, abs(fromCenter.x - 0.2 * wind * sin(2.0*fromCenter.y)))) * (1.0 - smoothstep(0.0, 0.1, fromCenter.y));

	fragColor = vec4(col, 1.0);
}
