// inspired from  aiekick's https://www.shadertoy.com/view/4sKXzy

void mainImage( out vec4 o, vec2 u )
{ 
    u *= mat2(1,-1./1.73, 0,2./1.73) *5./ iResolution.y;  // conversion to 
  //vec3 g = vec3(u,1.-u.x-u.y);                          // hexagonal coordinates
    vec3 g = vec3(u,fract(iDate.w/3.+u.x*.1)-u.x-u.y); 
	g = abs(fract(g)-.5);
	o += max(max(g.x, max(g.y,g.z)), min(g.x, min(g.y,g.z))*2.) -o;
  //o += min(max(g.x, max(g.y,g.z)), min(g.x, min(g.y,g.z))*2.) -o;
  //o += max(g.x, max(g.y,g.z)) -o;
  //o += min(g.x, min(g.y,g.z)) -o;


    // --- lookup table ------------------

#define s(x) (.5+.5*sin(x))
  //o = .5+.5*sin(20.*o);                               // arabic version
  //o = .5+.5*sin(20.*o * sin(iDate.w*.4)); 
  //o = .5+.5*sin(10.*o + vec4(0,2.1,-2.1,0));         // psychedelic version
  //o = .5+.5*sin(10.*o + vec4(0,2.1,-2.1,0) * s(iDate.w*.4));  
    o = mix(o,  s(10.*o + vec4(0,2.1,-2.1,0)), s(iDate.w*.4));
}
