void mainImage (out vec4 o, in vec2 i) {
    vec2 uv = i/iResolution.xy;
    vec2 m = iMouse.xy/i*uv;

    vec3 t = texture(iChannel0, uv).rgb;

    // Strength
    // This isn't a standard luminosity or HSV value amount but it looks better than they do.
    //
    float s = length(t);
    // Using dot is also an interesting look but higher contrast.
    //float s = dot(t,t);

    // Edge
    //
    float e = sqrt(fwidth(s));
    // Using pow gives more options to customize the edge balance.
    //float e = pow(fwidth(s), 0.5);

    // Posterize
    //
    float c = 5.;
    float p = floor(s * c) / c;

    // Value
    //
    float v = p * (1. - e);

    o.rgb = uv.y < m.y ? t : (uv.x < m.x ? vec3(v) : v * t);
    o.a = 1.;
}
