void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // use this image as the distortion
    vec4 img1 = texture(iChannel0, uv);

    // a val between -0.1 and +0.1
    float amt = sin(iTime)*0.1;

    // we just use the red and green channels for distortion
    // move them into a -1.0 to 1.0 range and mult by amt
    vec2 disp = (img1.rg*2.0 - 1.0) * amt;

    // add the displacement to the uv's texcoords
    vec4 img2 = texture(iChannel1, uv + disp);

    // Output to screen
    fragColor = img2;
}
