void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord * 5.0 / iResolution.xy - 1.0;
    float d = 0.0;
    for (float i = 0.0; i < 200.0; ++i) {
        float j = max(0.0, 3.14 - distance(uv, vec2(
            sin(i + iTime * mod(i * 2633.2363, 0.42623)) * 12.0,
            cos(i  * 0.617 + iTime * mod(i * 36344.2363, 0.52623)) * 12.0
        )));
        d += cos(j);
     }
    float r = cos(d * 6.0) * 0.5 + 0.5;
    float g = cos(d * 3.0) * 0.5 + 0.5;
    fragColor = vec4(r, g, max(r, g), 1.0);
}
