//     Ausgabe: Farbwert (r,g,b,a), Eingabe: (x,y)-Koordinate
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalisierter Koordinaten-Vektor
    vec2 uv = fragCoord / iResolution.xy;

    // Farbwert
    vec3 col = vec3(uv.x, uv.y, sin(iTime) / 2.0 + 0.5);

    // Neuer Pixelwert
    fragColor = vec4(col,1.0);
}
