vec2 conv(vec2 fragCoord)
{
    return (fragCoord-iResolution.xy/2.0)/min(iResolution.x, iResolution.y);
}
vec2 conv1(vec2 xy)
{
    return xy*min(iResolution.x, iResolution.y)/iResolution.xy + 0.5;
}
float toTheta(float L)
{
    return -(1.0-L)*8.0;
//    return -1.2/(L+0.08);
//    return log(L+0.00001);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = conv(fragCoord);
    float l = length(uv);

    float theta = iTime;	// blink effect
    theta = toTheta(l) + atan(uv.y, uv.x)* 2.0 + iTime * -3.0;
    vec2 pos = vec2(cos(theta), sin(theta));
    fragColor = texture(iChannel0, conv1(pos));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,1.0,1.0);
}
