#define BG vec3(.15, .25, .35)
#define S 10. // Scale


float noise(vec2 uv) {
	return fract(sin(dot(uv.xy ,vec2(532.1231,1378.3453))) * 53211.1223);
}


void mainImage(out vec4 c, vec2 p)
{
    vec2 uv1 = p / iResolution.y;
	vec2 uv2 = floor(S * uv1);
	c = vec4(vec3( mod(uv2.x + uv2.y, 2.))*0.05 + BG + noise(uv1)/16., 1.);
}
