void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color




    // Output to screen
    //fragColor = vec4(col,1.0);
    vec2 coord = fragCoord * 0.0018 + vec2(-0.2, 0.1);
    vec4 t = texture(iChannel1, coord);
    float avg = (t.x + t.y + t.z)/3.0;
    //if (avg < mod(iTime/5.0,1.0))
    if(avg < 0.4)
    {
        t = vec4(texture(iChannel1, coord + 0.02*vec2(cos((coord.y + iTime*0.06)*50.0),1.0)));
    }
    //if (avg < mod(iTime+1.0/5.0,1.0))
    else if(avg >= 0.6)
    {
        t = vec4(texture(iChannel1, coord + 0.01*vec2(sin((coord.y + 1.0 + iTime*0.06)*50.0),1.0)));
    }
    else
    {
     	t = vec4(1.0,1.0,1.0,1.0) - vec4(texture(iChannel1, coord));
    }



    fragColor = t;
}
