float wave(float x, float s) {
	return sin(x + mod(iTime * s, 3.1415 * 2.))/2. + .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y * 2. - 1.;
	uv.x -= (iResolution.x - iResolution.y) / iResolution.y;

	float t = mod(iTime* 2., 3.1415 * 2.);
	float a = 0.;
	for(float i = 1.; i <= 3.; ++i) {	
		a += sin(t * (i * 2. - 1.)) / (i * 2. - 1.);
	}
	a = a * 1.15 / 2. + .5;

	float power = 2. / (1. - min(a, .98));
	float x = pow((1.+2.*a)*abs(uv.x), power);
	float y = pow(abs(uv.y), power);
	float r = iResolution.y / 2.;
	float v = pow(x+y, 1./power);
	float l = (1. - v) * r;
	float l2 = (r/2. - l*(1.-a));
	float s = clamp(min(l, l2), 0., 1.);
	vec4 color = vec4(1,1,1,1); // vec4(wave(uv.x,3.), wave(uv.y,5.), wave(uv.x*uv.y,7.),1.0);
	fragColor = color * s;
}
