void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

	vec2 screenCoord = fragCoord.xy;

	float slowdownPerc = 0.314;
	float rad = sin(iTime * slowdownPerc) * iResolution.x; 

	vec2 toMiddle = iResolution.xy * 0.5 - screenCoord;
	float lengthToMiddle = toMiddle.x * toMiddle.x + toMiddle.y * toMiddle.y;
	if(lengthToMiddle > rad * rad)
	{
		fragColor = vec4(0,0,0,0);
	}


}
