void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}

float triangle(float f, float a, float t){
    return abs( mod(t*f*a, 2.0*a) - a ) * a - a;
}

float square(float f, float a, float t){
    return floor(mod(-t*4.0*f, 2.0)) * 2.0*a - a;
}

float wave(float f, float a, float t){
	return sin(t * f * 6.2831) * a;
}

vec2 mainSound( in int samp, float time )
{
    time = mod(time, 3.0);
    float freq = 440.0;   

    if( time <= 1.0){
        return vec2( wave(freq, 1.0, time) );
    }else if( time > 1.0 && time <= 2.0){
        return vec2( triangle(freq, 1.0, time) );
    }else{
        return vec2( square(freq, 1.0, time) );
    }
}
