// variant of https://www.shadertoy.com/view/ldlyD2
// golfed and inspired by https://www.shadertoy.com/view/lslcWj

/**/
void mainImage(out vec4 O, vec2 U) {
    float t = iTime, a,b; // s =  10.; 
    mat2  R = mat2( sin(t+1.57*vec4(1,2,0,1)) );
    vec3  q = iResolution,
          D = vec3((U+U-q.xy)/q.y, -1),
          p = 15./q;  // 1.5*s;

    for ( O+=1.-O; O.x > 0. && t > .01 ; O-=.015)
        q = p,
        q.xz *= R,
        q.yz *= R,
        t = max( length(q) - 9.,  // -s                         // Sphere
                 abs(a=cos(b=3.*dot(sin(q),D/D)))/9. ), // /s   // EggCarton
        p += t*D;

    O.r -= b/30.;
    O.g -= a;
}
/**/





