#define MAX_SHAPE_COUNT 3
uniform int uniform_shapeTypeList[MAX_SHAPE_COUNT];
uniform vec2 uniform_posList[MAX_SHAPE_COUNT];
uniform vec2 uniform_sizeList[MAX_SHAPE_COUNT];


bool IsVisibleSize( const vec2 size )
{
	return size.x >= 0.0 && size.y >=  0.0;
}

bool InRectangle( vec2 pos, vec2 size )
{
	if( fragCoord.x < pos.x - size.x
		|| fragCoord.x > pos.x + size.x )
	return false;

	if( fragCoord.y < pos.y - size.y
	   || fragCoord.y > pos.y + size.y )
		return false;

	return true;
}

bool InCircle( vec2 pos, vec2 size )
{
	float r = min( size.x, size.y);	
	vec2 offset = fragCoord.xy - pos;
	return sqrt( offset.x*offset.x + offset.y*offset.y ) <= abs(r);
}


bool TestShapeIntersection( int shapeType, vec2 pos, vec2 size  )
{	
	//Verify if the size can be intersected
	if( !IsVisibleSize( size ) )
	   return false;

	//Intersect with a rectangle	
	if( 0 == shapeType )
		return InRectangle( pos, size );

	//Intersect with a circle
	if( 1 == shapeType )
		return InCircle( pos, size );

	return false;
}


