#define PI 3.14159
precision lowp float;
float t;

vec2 cMul(vec2 a, vec2 b) {        
        return vec2(a.x*b.x - a.y*b.y,a.x*b.y + a.y * b.x);        
}
mat2 R(float a) {
		return mat2(vec2(cos(a), sin(a)), vec2(-sin(a), cos(a)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    t = iTime + 63.0;
	vec2 z = 2.0 * fragCoord.xy / iResolution.xy - 1.0, a;
	z.y *= iResolution.x/iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
	a.s = texture(iChannel0, vec2(uv.x * 0.5, 0.25)).s;
	a.t = texture(iChannel0, vec2(uv.y * 0.5, 0.75)).s;
	vec2 c = vec2(0.2) * R(t) + sin(t*0.05) - 0.2 + a * 0.2;
	for(int i=0; i < 6; ++i) {
		z = cMul(z, z*sin(t*0.12)) * R(sin(t*0.22)*PI);
		z += c - a.t * 0.5 + a.s * 0.5 * cos(t * 0.2);
	}
	z = smoothstep(0.0, 1.0, z);
	fragColor.rg = (length(z) > 1.0) ? uv : z;
	fragColor.ba *= length(a) - 1.0 * uv;
}
