#define SPEED       1.0 //(1.0 / 40.0)
#define SMOOTH_DIST 0.3

#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set up our coordinate system
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 uv = ((fragCoord.xy / iResolution.xy) - vec2(0.5,0.5));
    uv.x *= aspectRatio;

	// get our distance and angle
    float dist = length(uv);
    float angle = (atan(uv.y, uv.x) + PI) / (2.0 * PI);

   	// texture lookup
    vec3 textureDist;
    textureDist  = texture(iChannel0, vec2(iTime * SPEED, angle)).xyz;
    textureDist /= 6.0;

    // set RGB
	vec3 color = vec3(0.0);

    float dist1 = textureDist.x;
    float dist2 = dist1 + textureDist.y;
    float dist3 = dist2 + textureDist.z;

   	#if 0
    if (dist < dist1)
        color = vec3(1.0);
    else if (dist >= dist2 && dist < dist3)
        color = vec3(1.0);
    #else
    if (dist < dist1)
        color = vec3(1.0,0.0,0.0);
    else if (dist < dist2)
        color = vec3(0.0,1.0,0.0);
    else if (dist < dist3)
        color = vec3(0.0,0.0,1.0);
    #endif

    // set final color
	fragColor = vec4(color,1.0);
}
