float xorshiftadd(void) {
	float s1 = state[ 0 ];
    float s0 = state[ 1 ];
    state[ 0 ] = s0;
    s1 += (s1 * 8388608.0); // << 23 is * 8388608 and + is a decent xor replacement
    return ( state[ 1 ] = ( s1 + s0 + ( s1 / 131072.0 ) + ( s0 / 67108864.0 ) ) ) + s0;
}


