void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
    vec2 q = p - vec2(.5);

	vec3 col = mix( vec3(0,0,1), vec3(1), (p.y) );

    float r = .2;
    float Step = smoothstep(r, r+.1, length(q));
    if ( Step < .5 )
    {
        col = (1.0-Step)*mix( vec3(1,0,0), vec3(1,.5,0), p.y + p.x );

        vec2 p2 = q/.2;
        vec2 Corners[4];
        Corners[0] = vec2(.5,.5);
        Corners[1] = vec2(.5,-.5);
        Corners[2] = vec2(-.5,.5);
        Corners[3] = vec2(-.5,-.5);


        for ( int i = 0; i < 4; i++ )
        {
            vec2 q2 = p2 - Corners[i];
            float r2 = .1 + .2*cos( atan(q2.y,q2.x)*5.0 );
            col *= smoothstep(r2, r2+.05, length( q2 ) );
        }
    }
    else if ( Step < 1.0 )
    {
        vec3 colr = mix( vec3(1,1,0), vec3(1), (Step-.5)*2.0 );
        col = mix( colr, col, (Step-.5)*2.0 );
    }

	fragColor = vec4(col,1.0);
}
