#define PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t =0.2*iTime;
    vec2  s =iResolution.xy/60.+abs(mod(t,8.)-4.);
    vec2  p =fragCoord.xy/iResolution.x*s-s/2.;
    float v =.0+sin((p.x+3.*t))+sin((p.y+6.*t)/2.)+sin((p.x+p.y+2.*t)/2.);
    p += s/2.*vec2(sin(t/3.),cos(t/2.));
    v += sin(sqrt(p.x*p.x+p.y*p.y+1.)+t);
    float R =sin(.2*PI*v),G =cos(.75*PI*v),B =sin(.9*PI*v);

    // restrict the color palette
    R = ceil(R*255. /  8.) *  8. / 256.;
    G = ceil(G*255. / 16.) * 16. / 256.;
    B = ceil(B*255. /  8.) *  8. / 256.;

    if(mod(R,16.) < 1.) R =G*.5+.5; // less red (more green, blue & yellow)
    vec3 col =vec3(R,G,B);

    fragColor = vec4(col,1.);
}
