void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const float Strength = 0.125;    
    const int Samples = 64; //multiple of 2

    vec2 uv = fragCoord.xy/iResolution.xy*vec2(1.0,-1.0);

	vec2 dir = (fragCoord.xy-iMouse.xy)/iResolution.xy*vec2(-1.0,1.0);

    vec4 color = vec4(0.0,0.0,0.0,0.0);

    for (int i = 0; i < Samples; i += 2) //operating at 2 samples for better performance
    {
        color += texture(iChannel0,uv+float(i)/float(Samples)*dir*Strength);
        color += texture(iChannel0,uv+float(i+1)/float(Samples)*dir*Strength);
    }   

    fragColor = color/float(Samples);
}
