void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 texCoord1=vec2(fragCoord.x/iResolution.x,fragCoord.y/iResolution.y);
    float time=iTime;
    float cx=texCoord1.x-0.5;
    float cy=texCoord1.y-0.5;
    vec4 color=texture(iChannel0, vec2(cx,cy));

    if (cx<0.0)cx*=-1.0;//=1.0-cx;

    float angle = atan(cy,cx);
    float whiteness=2.5+sin(time*4.0+angle*50.0)*2.0;
    cy=clamp(cy*3.0,0.0,1.0);
    whiteness=0.0+clamp(whiteness,0.5,1.7);
    whiteness=mix(1.0,whiteness,cy);
    color.rgb*=whiteness;
    fragColor = color;
}
