//#define ANTIALIASING

vec3 ambientLight = vec3(.2 + .2 * sin(1.), .2 + .2 * cos(1.), 0.2) * .2;
vec3 dirLightColor = vec3(0.5, 0.5, 0.5) * 3.0;
vec3 dirLightDir;
float c = 2.0;

float dBox(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0)) - 0.07;
}

float map(vec3 p)
{
    vec3 b = vec3(0.5);
    vec3 q = vec3(
        mod(p.x, c) - 0.5 * c,
        mod(p.y, c) - 0.5 * c,
        mod(p.z, c) - 0.5 * c);
    return dBox(q, b);
}

vec3 getNormal(vec3 pos)
{
    float threshold = 0.001;
	vec2 t = vec2(0.0, threshold);
    vec3 n = vec3(map(pos + t.yxx) - map(pos - t.yxx),
           	  map(pos + t.xyx) - map(pos - t.xyx),
              map(pos + t.xxy) - map(pos - t.xxy));
    return normalize(n);
}

bool getDepth(vec3 rayPos, vec3 rayDir, inout float depth)
{
    const int max_iters = 1000;
    float phase = (.5 + .5 * sin(iTime));
    //float eps = mix(0.001, 0.5, phase);
    float eps = 0.001;
    vec3 p = rayPos;
    float d;
    float d_total = 0.0;
    for (int i = 0; i < max_iters; ++i)
    {
        d = map(p);
        d_total += d;
        p += d * rayDir;
        if (d < eps)
            break;
    }
    depth = d_total;
    return d < eps;
}

bool intersect(vec3 rayPos, vec3 rayDir, inout vec3 color, inout vec3 normal, inout float d)
{
    vec3 bg = vec3(0.1, 0.1, 0.1);

    float depth;
    bool hit = getDepth(rayPos, rayDir, depth);
    d = depth;
    normal = vec3(0.0);
    vec3 hitPoint = rayPos + depth * rayDir;
    if (hit)
    {
        normal = getNormal(hitPoint);
        float colorMod = c * 2.0;
        //vec3 kd = mod(hitPoint, colorMod) / colorMod;
        vec3 kd = vec3(0.0);            

        vec3 ka = ambientLight;
        color = ka + dirLightColor * kd * max(dot(normal, -dirLightDir), 0.0);
        return true;
    }
    else
    {
	    color = bg;
        normal = vec3(0.0);
        return false;
    }

}

