void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    dirLightDir = normalize(vec3(sin(iTime), -0.3, 1.0));
	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;

    uv -= .5;
    uv.x = aspect * uv.x;

    vec3 camPos = vec3(2.0, 1.5, -2.) * iTime;
    vec3 camTarget = vec3(0.);
    vec3 camUp = vec3(0., 1., 0.);
    vec3 camDir = normalize(camTarget - camPos);
    vec3 camRight = normalize(cross(camUp, camDir));
    camUp = normalize(cross(camDir, camRight));
    vec3 rayPos = camPos;
    vec3 rayDir = normalize(camDir + uv.x * camRight + uv.y * camUp);

    float zNear = 5.0;
    rayPos += zNear * rayDir;

    vec3 c;
    vec3 n;
    float d;//todo eeppiset fireballit (perlin / metaball)

    #ifdef ANTIALIASING
    vec3 ct = vec3(0.0);
    vec2 offset = 
    intersect(rayPos - offset.x * camRight - offset.y * camUp, rayDir, c, n, d); ct += c;
    intersect(rayPos - offset.x * camRight + offset.y * camUp, rayDir, c, n, d); ct += c;
    intersect(rayPos + offset.x * camRight - offset.y * camUp, rayDir, c, n, d); ct += c;
    intersect(rayPos + offset.x * camRight + offset.y * camUp, rayDir, c, n, d); ct += c;
    ct /= 4.0;
    c = ct;
    #else
    intersect(rayPos, rayDir, c, n, d);
    #endif

    //c = mix(c, mix(texture(iChannel0, uv).xyz, vec3(1.0, 0.0, .5 + .5 * sin(iTime * 0.3)), 0.3), d);    
    c = mix(c, mix(texture(iChannel0, uv).xyz, vec3(0.8, 0.0, 0.0), 0.3), d);    

	fragColor = vec4(c, 1.0);
}
