void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec4 color = vec4(0,0.0,0.0,1.0);

	vec2 xy = fragCoord.xy;
    color.g = abs(sin(xy.x)*5.0+sin(iTime));//Set its red component to 1.0

    color.r = abs(sin(xy.y/10.0 + cos(xy.x/20.0+tan(iTime))));
    fragColor = color;
}
