/*
Sound Experiment
Copyright 2017 Theron Tarigo
*/

const float pi = 3.141592653589793;

float wave(float t) { return sin(2.*pi*fract(t)); }

float synth(float freq, float freq2, float time) {

    float freq3 = freq2 + 0.25;

    float v = 0.;

    for (float n=1.; n<5.; n+=1.00+0.002) {
        v += 0.2/pow(n,1.2)*wave(n*freq*time+1.*wave(n*freq2*time));
        v += 0.2/pow(n,1.2)*wave(n*freq*time+1.*wave(n*freq3*time));
    }
    return v;
}

vec2 mainSound( float time )
{

    float c = 261.63;

    vec2 v = vec2(0.);

    v.x += synth(c*2., c*2./8., time);
    v.y += synth(c*2., c*2./8., time+1.2);

    v *= max(1.-exp(-time+0.5),0.);

    return vec2(v);
}

/*******************************/
/*
Streaming Spectrogram
Copyright 2017 Theron Tarigo
*/

vec2 spectrogram(float windowmax, float time, float freq) {
    vec2 sum = vec2(0.);
    float window = windowmax;
    float samplerate = iSampleRate/6.;
    for (float trel = -window; trel <= window; trel+=1./samplerate) {
        // Cosine window
        float w = (0.5+0.5*cos(trel/window*pi))/samplerate/window;
        float samp = dot(vec2(1.),mainSound(time+trel));
        sum += w*vec2(cos(trel*freq*2.*pi),sin(trel*2.*pi))*samp;
    }
    return sum;
}

void panImage( out vec4 f, float time, float y ) {
    float freq = 4000. * y / iResolution.y;
    f.rgb = 5.*vec3(10.,3.2,1.)*
    length(spectrogram(0.1*sqrt(freq/4000.), time, freq));
}

const float timescale = 1./4.; // Screens per second

//#define FRAMEDEBUGVIS
void mainImage( out vec4 f, in vec2 df )
{
    ivec2 d = ivec2(df);
    vec2 fsf = iResolution.xy;
    ivec2 fs = ivec2(fsf);
    int shift = int(fsf.x*timescale*(iTime));
    int shiftlast = int(fsf.x*timescale*(iTime-iTimeDelta));
    int delta = shift - shiftlast;
    if ((fs.x-d.x) <= delta) {
        #ifdef FRAMEDEBUGVIS
        if (d.y<10) { f = vec4(0,iFrame%2,0,0); return; }
        #endif
        float time = float(shift-fs.x+d.x)/timescale/fsf.x;
        panImage(f, time, df.y);
    } else {
        f = texelFetch(iChannel0,d+ivec2(delta,0),0);
    }
}

