#define M_PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float y = fragCoord.y / iResolution.y;
    float t = iTime * 6.0;
    float tr = 0.5 + 0.5 * sin(t * 1.0 + y * M_PI * 6.0 + 0.0 * M_PI);
    float tg = 0.5 + 0.5 * sin(t * 1.1 + y * M_PI * 6.0 + 0.5 * M_PI * 6.0);
    float tb = 0.5 + 0.5 * sin(t * 1.2 + y * M_PI * 6.0 + 1.5 * M_PI * 36.0);
	fragColor = vec4(tr, tg, tb, 1.0);
}
