void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 xy = 2. * (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;
	fragColor = vec4(1.);
    vec4 green = vec4(0., 1., 0., 1.);

    xy = xy * (1.6 - abs(sin(iTime)));
    xy.x += sin(iTime * 1.1) / 1.75;
    xy.y -= sin(iTime) / 10.;

    const float radius = .5;
    float m = smoothstep(radius - .005, radius + .005, length(xy));

    fragColor = mix(green, fragColor, m);
}
