// variant of Greg's https://www.shadertoy.com/view/MsSBDR

#define F fract(iTime

void mainImage(out vec4 o, vec2 u)
{
    vec2 R = iResolution.xy, U;
    o = 3. * texture(iChannel1, u/R);
    U = u / dot( u = u / R.x - .5, u);

    o = mat4( max( U= F +U+U), U.x - U ).yyyy,
              F+ u.x*u.yyyy*2e3 ),
              o.g - o.rrrr,
              texture( iChannel0, F *.2 - vec2(u.x,1)/u.y ) )* -u.y*3.
        ) * sin( F*.1)*6.3 + vec4(0,2.1,-2.1,0) );
}
