#define T texture( iChannel0, u = u * 2. + iTime * .07 ).x

void mainImage( out vec4 Y, vec2 X )
{
    vec2 u = iResolution.xy,
         p = ( X + X - u ) / u.y / .9;

    float d = dot( p, p );

    vec3 n = vec3( p, sqrt( 1. - d ) );
    u = n.xy / sqrt( n.z ) / 4.;

    Y = d > 1. ? 
            exp( -.3 * d ) * vec4( .3, .4, .5, 1 )
            : 
            T * vec4( .9, .9 - .3 * ( n.z += T * .5 ), .3, 1 ) + texture( iChannel1, n ) * .5;
}
