float rand(vec2 v) {
    return fract(sin(dot(v, vec2(13.443643, 32.67963)))*213994.78075);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float rn = 10.0;
    float basev = 10.0;
    float row = floor(uv.y*rn)/rn;

	fragColor = 1.0-vec4(step(0.5, rand(vec2(fragCoord.x+floor(iTime*basev*(2.0*mod(row*rn, 2.0)-1.0)), row))),
                        step(0.5, rand(vec2(fragCoord.x+floor((iTime+2.0/basev)*basev*(2.0*mod(row*rn, 2.0)-1.0)), row))),
                        step(0.5, rand(vec2(fragCoord.x+floor((iTime-1.0/basev)*basev*(2.0*mod(row*rn, 2.0)-1.0)), row))),
                        1)*step(0.1, fract(uv.y*rn));
}
