vec4 black = vec4(0,0,0,1);
vec4 white = vec4(1,1,1,1);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    highp int intX = int(fragCoord.x);

    float count = 20.0;

    float xl = floor (iResolution.x / count);
    float yl = floor (iResolution.y / count);
    highp int mouseX = int(iMouse.x);
    float d1 = distance(iMouse.xy, fragCoord.xy);
    float d2 = distance(iMouse.xy, fragCoord.xy);

    float T = 80.0;


    if (iMouse.x >= fragCoord.x && iMouse.y <= fragCoord.y) {
    	d1 = d1 * -1.0;
    }

     if (iMouse.x >= fragCoord.x && iMouse.y < fragCoord.y) {
    	d1 = d1 * -1.0;
    }

    if (iMouse.y <= fragCoord.y && iMouse.x <= fragCoord.x) {
    	d2 = d2 * -1.0;
    }

    if (iMouse.y <= fragCoord.y && iMouse.x < fragCoord.x) {
    	d2 = d2 * -1.0;
    }


    if(d1 > T || d1 < -T){
    	d1 = 0.0;
    }

    if(d2 > T || d2 < -T){
        d2 = 0.0;
    }

    float oX = d1 * (d1/T) * tan(iTime/2.0);
    float oY = d2 * (d2/T) * tan(iTime/2.0);


    bool colIsEven = mod(floor((fragCoord.x + oX) / xl), 2.0) == 0.0;
    bool rowIsEven = mod(floor((fragCoord.y + oY) / yl), 2.0) == 0.0;

    if (colIsEven && !rowIsEven || (!colIsEven && rowIsEven)) {
    	fragColor = white;
        return;
    }

	fragColor = black;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,1.0,1.0);
}
