//Ethan Alexander Shulman 2017


#define ITERATIONS 24
#define BIAS vec3(0., 0., 0.)
#define BRIGHTNESS 24.0
#define GAMMA 2.



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 rayDir = normalize(vec3((fragCoord*2.-iResolution.xy)/iResolution.x, 1.)),
         rayPos = vec3(sin(iTime*0.0133), cos(iTime*0.01), -2.);

    vec3 sum = vec3(0.);
    for (int i = 0; i < ITERATIONS; i++) {

        vec3 samplePos = rayPos+rayDir*(float(i*i)/(float((ITERATIONS-1)*(ITERATIONS-1)))*5.625);
        vec2 apos = vec2(atan(samplePos.x,samplePos.z)+iTime*0.077,
                         atan(samplePos.z,samplePos.y)+iTime*0.03);

        sum += clamp(texture(iChannel0, cos(apos)*0.5+0.5).xyz+BIAS,0.,1.)/length(samplePos);

    }

    fragColor = vec4(pow(sum/float(ITERATIONS), vec3(GAMMA))*BRIGHTNESS,1.);
	//fragColor = texture(iChannel0, fragCoord/iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (fragCoord.y < 1.) {
        float s = pow(texture(iChannel3, vec2(fragCoord.x/iResolution.x,0.4)).x, 2.2);
        fragColor = vec4(s,s*0.3,s*0.5,s*0.7);
        return;
    }

    fragColor = texture(iChannel0, (fragCoord+vec2(0.,-1.))/iResolution.xy).wxyz;
}
