// constant thickness by iq: https://www.shadertoy.com/view/MdfGWn
// udRoundBox by iq too.

#define rgb(r,g,b) vec3(r,g,b)/255.

float wave1(vec2 p)
{
    return length(p - vec2(p.x, .4 * cos(p.x*3. + iTime)));
}

float wave2(vec2 p)
{
    float f = wave1(p);
    float e = 2.0/iResolution.y;
    float g = length( vec2(wave1(p+vec2(e,0.0))-wave1(p-vec2(e,0.0)),
                           wave1(p+vec2(0.0,e))-wave1(p-vec2(0.0,e))) )/(2.0*e);
    return f/ g;
}

float udRoundBox( vec2 p, vec2 b, float r )
{
    return length(max(abs(p)-b,0.0))-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy) / iResolution.y;

    uv *= 2.;

    float f0 = wave2(uv + vec2(0., -1.1));
    float f1 = wave2(uv + vec2(0., -.4));
    float f2 = wave2(uv + vec2(0., .4));
    float f3 = wave2(uv + vec2(0., 1.1));

    float f = min(min(min(f0,f1),f2),f3);
    f = 1.0-smoothstep( 0.1, 0.11, f );

    uv *= 0.5;

    float f6 = 1.-smoothstep( 0.1, 0.107,udRoundBox(uv, vec2(0.5,0.5),0.25));
    float f7 = 1.-smoothstep( 0.1, 0.107,udRoundBox(uv, vec2(0.5,0.5),0.35));
    float f8 = f7 - f6;

    f = min(f,f6);
    f = f + f8;

    vec3 c = (1. - f) * vec3(.97) + f * rgb(0.,172.,242.);

	fragColor = vec4(c,1.0);
}
